INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('Microfono professionale AU769-KK','Il nuovo microfono professionale AU769-KK rende possibile l''impensabile: rimuove la il rumore emesso da chi ha una pessima voce. E'' tutto automatico, premi su On, poi sul bottone sottostante, quindi passi il microfono al cantante di turno.','Tecnologie audio',1,TRUE,NULL,NULL,0,0);
