INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('Colonna multistereo MSCo','La colonna multistereo permette la riproduzione contemporanea di decine di brani, ognuno in una sua direzione. Utile per le famiglie che non riescono mai a mettersi d''accordo','Tecnologie audio',1,TRUE,NULL,NULL,0,0);
INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('Cuffie Bluetooth 3 con Cancellazione del Rumore Adattiva','Le WaveSound 3 sono considerate le cuffie migliori per i viaggi in aereo perché sono versatili e sono dotate di un comodo adattatore per aereo. Basta semplicemente collegare l''adattatore alla presa dell''aereo per ascoltare il film o lo spettacolo che viene riprodotto sullo schermo. In base alle tue preferenze, se desideri ascoltare musica o guardare video / film sul tuo Smartphone o laptop, è possibile collegare il cavo ausiliario fornito con le cuffie nel tuo dispositivo. Le cuffie si piegano verso l''alto e sono accompagnate da una custodia da trasporto compatta, il che significa che non occuperanno molto spazio nella tua borsa, nel tuo zaino o nel tuo bagaglio a mano. Già solo questa caratteristica rende le nostre cuffie Real Tone un''eccellente idea regalo per studenti, dirigenti, adolescenti e così via.','Tecnologie audio',1,TRUE,NULL,NULL,0,0);
INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('Cuffie Wireless Air Bluetooth Originali con Capsula di Ricarica, Compatibile con Bluetooth 5.0','Air è l''auricolare wireless più piccolo e colorato al mondo, con audio HiFi, Bluetooth 5.0, 2,5 volte più veloce, attacco sicuro, microfono in-ear e capsula di ricarica di 12 ore. Progettato dal team vincitore del Red Dot Award. Air utilizza un design classico a proiettile, che li fa facile da indossare in tutti i tipi di orecchie. Per garantire che siano confortevoli, abbiamo studiato le orecchie di centinaia di persone per determinare la perfetta inclinazione, angolazione e adattamento di Air . Abbiamo puntato alla curvatura ergonomica della superficie degli auricolari ideale per il massimo comfort. ','Tecnologie audio',1,TRUE,NULL,NULL,0,0);
