INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('CMW2070DW Microonde con display, 20 litri','Grazie al programma Defrost è sufficiente selezionare il peso dell''alimento da scongelare e il forno a microonde penserà a tutto: il risultato sarà semplicemente perfetto. Premendo semplicemente un tasto, Start Express imposta automaticamente il forno all''ottima potenza per 30 secondi. Ogni volta che il bottone viene premuto vengono aggiunti altri 30 secondi al tempo di cottura. Una funzionalità molto semplice, intuitiva e utile. La funzione Blocco bambini evita che il forno venga avviato accidentalmente, soprattutto dai bambini. Quando la funzione è attiva, può essere disattivata solo da un adulto, assicurando così la ottima sicurezza in cucina. ','Elettrodomestici',1,TRUE,NULL,NULL,0,0);
