INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('CS4 H7A1DE-S Asciugatrice Slim, 7 kg, Bianco','Risparmio di spazio unico. Mancanza di spazio? Candy ha la soluzione per te: un''asciugabiancheria adatta anche agli angoli più stretti. Sappiamo quanto possa essere un problema di spazio lavare e asciugare il bucato. Per questo motivo abbiamo creato la soluzione che stavi aspettando: un''asciugatrice unica profonda solo 46 cm che asciuga fino a 7kg e che si può impilare perfettamente sulla tua lavatrice, dandoti più spazio con gli stessi risultati perfetti e lo stesso risparmio energetico. Scegli il momento migliore per asciugare il tuo bucato. La nuova asciugabiancheria Candy è realizzata per semplificarti la giornata, ecco perché può effettuare i cicli di asciugatura in base alle tue preferenze, ritardando la partenza fino a 23 ore. Con questa impostazione, puoi far partire il ciclo durante la notte, approfittando dei bassi consumi, o quando sei al lavoro, in modo che tu possa trovare il tuo bucato pronto una volta rientrato a casa. Dimezza le pieghe, dimezza il tempo di stiratura. ','Elettrodomestici',1,TRUE,NULL,NULL,0,0);
INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('FD22G Scopa Ricaricabile 2 in 1 Freedom ','FREEDOM Freedom è la nuova scopa senza cavo, concepita per darti il massimo comfort e la massima flessibilità durante l''utilizzo. Grazie all''aspirabriciole integrato e ad una serie di accessori, è un vero alleato per ogni esigenza di pulizia. MODALITÀ Per una lunga sessione di pulizia, non c''è bisogno di tenere premuto il tasto di accensione continuamente: semplicemente tira la levetta e la scopa resterà accesa con un semplice tocco. ASPIRABRICIOLE INTEGRATO Facile da staccare, l''aspirabriciole è ideale per pulizie quotidiane di tutte le superfici sopra ai pavimenti e per raggiungere gli spazi più stretti. COMPLETAMENTE ACCESSORIATO Freedom 2in1 è dotato di una serie di accessori che rendono il prodotto versatile e flessibile. E'' un vero alleato per ogni esigenza di pulizia.','Elettrodomestici',1,TRUE,NULL,NULL,0,0);
