INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('RH8121WB Powerline Extreme Bagless, Scopa elettrica con Filo','Risparmio energetico di classe A, prestazioni di classe A, filtraggio di classe A. La scopa elettrica Powerline è perfetta per esperti dei sistemi di pulizia tradizionali che desiderano pulire a fondo la casa in maniera efficace. Powerline ha superato i testi dell''etichetta energetica europea con punteggi ottimi, ottenendo una A per i pavimenti duri e piatti, per il consumo energetico e per il filtraggio. La scopa elettrica con filo Powerline Extreme Cyclonic garantisce un''efficace rimozione della polvere dalle superfici dure e piatte quali i pavimenti di legno o piastrelle e risultati di filtraggio ottimali, limitando al contempo il consumo di energia. ','Elettrodomestici',1,TRUE,NULL,NULL,0,0);
INSERT INTO APP.PRODUCTS (NAME,DESCRIPTION,CATEGORY,CREATOR,IS_PUBLIC,LOGO,PHOTO,NUM_VOTES,RATING) VALUES ('Forno Elettrico con Capacità di 23 Litri, Potenza 1500 W','Mini forno e grill Aicook 23L cuociono in totale comodità, aiutandoti a raggiungere il risultato perfetto che miri a raggiungere. La soluzione ideale per cucine più piccole, il mini forno offre tutte le funzionalità di un forno convenzionale in un solo apparecchio compatto e facile da usare.Il timer da 60 minuti con segnale di fine cottura garantisce precisione e precisione. Selettore di temperatura di grado 100 ? -230 ?. Fermentazione della pasta 100-150 ? e conservazione degli alimenti. Biscotti 150-180 ? cottura, Cottura torta 180-200 ° C. 200-230 ? pizza, pesce e pollo.','Elettrodomestici',1,TRUE,NULL,NULL,0,0);
